sys_ux_client_script.script:
function handler({
    api,
    event,
    imports
}) {
    var tableName = api.context.props.table;
    var id = event.payload.item.id;
    var route = "kb_view";

    var redirectPayload = {
        "redirect": '',
        "route": route,
        "title": "Knowledge",
        "fields": {
            "table": tableName,
            "sysId": id
        },
        "targetRoute": "current"
    };
    api.emit("NAV_ITEM_SELECTED", redirectPayload);
}
