sys_ux_data_broker_transform.script:
function transform(input) {
    gs.error("MEBS input value " + input.testSuite);
	try {
        var inputs = {};
        inputs['suite_to_run'] = input.testSuite; // String 

        // Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
        //sn_fd.FlowAPI.getRunner().subflow('x_snc_simulator_sa.execute_test_suite').inBackground().withInputs(inputs).run();
gs.error("MEBS subflow launched with (in try) " + input.testSuite);
        // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
        var result = sn_fd.FlowAPI.getRunner().subflow('x_snc_simulator_sa.execute_test_suite').inForeground().withInputs(inputs).run();
        var outputs = result.getOutputs();


		gs.error("MEBS subflow result " + result);
        // Get Outputs:
		// Note: outputs can only be retrieved when executing synchronously.
		var all_feedback = outputs['all_feedback']; // String	

    } catch (ex) {
        var message = ex.getMessage();
        gs.error("MEBS subflow:" + message);
    }
	return all_feedback;
}
